    search_str = search_str.replace('HTTP://', '')
    search_str = search_str.replace('WWW.', '')
    search_str = search_str.replace('WORLDCOMMUNITYGRID.ORG/BOINC/', 'WORLDCOMMUNITYGRID.ORG') # fix for WCG
